REPLACE INTO `quest_offer_reward_locale` VALUES ('5893', 'ruRU', 'А, вот они! Превосходно, $N! Припасы немного заплесневели – сказывается влажность воздуха в руднике – но все равно сгодятся.$B$BСпасибо тебе, $N. Ты $Gпомог:помогла; нашим войскам поддержать силы.', '0');
