REPLACE INTO `quest_offer_reward_locale` VALUES ('7876', 'ruRU', '<Капитан Лободроб одобрительно порыкивает.>$B$BИ сколько еще они смогут так продержаться? Сколько еще солдат Альянса должно погибнуть, чтобы они поняли, что пора отступать? Время покажет, $R, время покажет…', '0');
