REPLACE INTO `quest_offer_reward_locale` VALUES ('8787', 'ruRU', 'Отлично... Я раздам все комплекты солдатам. Легион Оргриммара благодарит тебя, $N!', '0');
REPLACE INTO `quest_offer_reward_locale` VALUES ('8788', 'ruRU', 'Похоже, этот подарок несколько раз встряхнули. Ярлычок гласит:$B$BСамому любимому |3-2($R) |3-2($C).', '0');
REPLACE INTO `quest_offer_reward_locale` VALUES ('8789', 'ruRU', 'Это оружие, усиленное элементием, одинаково легко пронзит как обычного киражи, так и их бога.', '0');
REPLACE INTO `quest_offer_reward_locale` VALUES ('8790', 'ruRU', 'Используй это оружие во благо, $N. Пусть киражи ощутят ту боль, которую они так жаждут причинить другим.', '0');
REPLACE INTO `quest_offer_reward_locale` VALUES ('8791', 'ruRU', 'Выражаю тебе нашу наиглубочайшую благодарность. Ты $Gоказал:оказала; огромную услугу жителям Калимдора. Можешь выбрать один из этих амулетов, $N.', '0');
