REPLACE INTO `quest_request_items_locale` VALUES ('9431', 'ruRU', 'Письмо магистра Элозай немного запутанно, но я, кажется, понял, что она хочет от меня. Если у вас есть земля, про которую она упоминала, то я могу положить ее в сосуд для воды из лунного колодца.', '0');
