REPLACE INTO `quest_request_items_locale` VALUES ('9451', 'ruRU', 'Вместе Сломленные и дренеи обретут более глубокое понимание тайн, скрываемых стихиями.$B$BТы $Gвернулся:вернулась; с тем, что необходимо для создания твоего земного тотема?', '0');
