REPLACE INTO `quest_request_items_locale` VALUES ('9493', 'ruRU', 'Я не настолько наивен, чтобы думать, что поражение легионеров Изувеченной Длани знаменует нашу победу над Ордой Скверны, но это хороший первый шаг.', '0');
