REPLACE INTO `quest_request_items_locale` VALUES ('9510', 'ruRU', 'Вернитесь к поставщику Бракнару в Земли маг\'харов, что на Полуострове Адского Пламени.', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('9512', 'ruRU', 'Гр-р-р... Яр-р-р...Лопни моя селезенка... Ой, прости, $gпарень:девица;, приходится балакать иногда по-пиратски, а то совсем разучусь...', '0');
