REPLACE INTO `quest_request_items_locale` VALUES ('9522', 'ruRU', 'Наши разведчики ночных эльфов сообщают, что подготовка к атаке, возможно, еще продолжается. Эти повелители ужаса должны умереть, $N!', '0');
