REPLACE INTO `quest_request_items_locale` VALUES ('9572', 'ruRU', '$N, правильно? Сток\'тон сказал, чтобы я ожидал твоего доклада.$B$BНадеюсь, что ты $Gпринёс:принесла; хорошие новости?', '0');
