REPLACE INTO `quest_request_items_locale` VALUES ('9574', 'ruRU', 'Ну как, удалось тебе найти нужные образцы?', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('9575', 'ruRU', '$N, точно? Чадвик говорил, что ждет твоего доклада.$B$BНадеюсь, ты $gпринес:принесла; хорошие новости?', '0');
