REPLACE INTO `quest_request_items_locale` VALUES ('10572', 'ruRU', '<Вы передаете материалы командиру звена.>', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('10574', 'ruRU', 'Ты $Gсобрал:собрала; все четыре фрагмента, $N?', '0');
