REPLACE INTO `quest_request_items_locale` VALUES ('10577', 'ruRU', 'Маскировка деликатная. Практически любое действие, кроме ходьбы, нарушит удерживающую его магию.', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('10578', 'ruRU', 'Поторопись!', '0');
