REPLACE INTO `quest_request_items_locale` VALUES ('10597', 'ruRU', '<Вы вручаете материалы Галмоку>', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('10598', 'ruRU', 'Тебе удалось уничтожить инферналов Легиона?', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('10600', 'ruRU', 'Вы уже проложили путь в Кузницу Смерти?', '0');
