REPLACE INTO `quest_request_items_locale` VALUES ('11218', 'ruRU', 'Кажется, мы никогда не закончим возводить эту крепость. Надеюсь, ты несешь нам припасы, необходимые для продолжения строительства.', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('11219', 'ruRU', '$N, вы обещали спасти детей!', '0');
