REPLACE INTO `quest_request_items_locale` VALUES ('11224', 'ruRU', 'Это серьезный удар по нам. Я даже не хочу знать, что они там обнаружили.$B$BЭтого достаточно, чтобы я не могла уснуть по ночам, просто пытаясь представить, что это могло быть.', '0');
