REPLACE INTO `quest_request_items_locale` VALUES ('11250', 'ruRU', 'Полагаю, раз ты $Gвернулся:вернулась; то Скорн больше нам не угрожает?', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('11252', 'ruRU', 'Рад видеть, что ты $Gздоров:здорова;, $N.$B$BУ нас было столько жертв и мы выдержали.', '0');
