REPLACE INTO `quest_request_items_locale` VALUES ('12540', 'ruRU', 'Поговорите с раненым оракулом к северу от Холма Бешеного Сердца, что в Низине Шолазар.', '0');
REPLACE INTO `quest_request_items_locale` VALUES ('12541', 'ruRU', 'Видишь, $N, настоящая алхимия – это не просто умение тупо смешивать ингредиенты...', '0');
